import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-saved-code',
  standalone: false,
  templateUrl: './saved-code.html',
  styleUrl: './saved-code.scss'
})
export class SavedCode {
  @Input() savedCode: { language: string; filename: string; content: string }[] = [];
}
<div>
  <h2 class="text-xl font-bold mb-2">View Saved Code</h2>
  <p class="text-sm text-gray-400 mb-4">
    Browse and manage your saved HTML, CSS, and JavaScript files.
  </p>

  <button class="bg-orange-600 hover:bg-orange-700 text-white px-4 py-2 rounded-md font-semibold">
    View Saved Code
  </button>
</div>
