import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-assignments',
  standalone: false,
  templateUrl: './assignments.html',
  styleUrl: './assignments.scss'
})
export class Assignments {
  @Input() assignments: { title: string; score: number; total: number }[] = [];
}
<div>
  <h2 class="text-xl font-bold mb-2">View Assignments</h2>
  <p class="text-sm text-gray-400 mb-4">Access and review the assignments that you’ve attempted.</p>

  <button class="bg-orange-600 hover:bg-orange-700 text-white px-4 py-2 rounded-md font-semibold">
    View Assignments
  </button>
</div>
