import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { AssignmentsOverviewDataDto } from '../../../../models/assignment.model';
import { DifficultyLevel, Technologies } from '../../../../models/Enums';
import { GuestAssignmentApiService } from '../../../../content/services/guest-api.service';

@Component({
  selector: 'app-all-assignments',
  standalone: false,
  templateUrl: './all-assignments.html',
  styleUrl: './all-assignments.scss'
})
export class AllAssignments {
  selectedTech: string = 'All';
  searchTerm: string = '';
  assignments : AssignmentsOverviewDataDto[] = [];
  technologies = Object.keys(Technologies).filter(key => isNaN(Number(key)));;
  difficultyLevels = Object.keys(DifficultyLevel).filter(key => isNaN(Number(key)));
  selectedDifficulty: string = 'All';

  difficultyMap = {
    [DifficultyLevel.Easy]: { label: 'Beginner', class: 'bg-green-100 text-green-700' },
    [DifficultyLevel.Medium]: { label: 'Intermediate', class: 'bg-yellow-100 text-yellow-700' },
    [DifficultyLevel.Hard]: { label: 'Expert', class: 'bg-red-100 text-red-600' },
  };
  
  constructor(private guestApiService: GuestAssignmentApiService, private route: ActivatedRoute, private router: Router) {}
    
  ngOnInit(): void {
    this.loadAssignments();
  }

get filteredAssignments() {
  return this.assignments.filter(a => {
    const techMatches = this.selectedTech === 'All' || Technologies[a.technology] === this.selectedTech;
    const difficultyMatches = this.selectedDifficulty === 'All' || DifficultyLevel[a.difficultyLevel] === this.selectedDifficulty;
    const searchMatches = !this.searchTerm || a.title.toLowerCase().includes(this.searchTerm.toLowerCase());
    return techMatches && difficultyMatches && searchMatches;
  });
}

getTechnologyLabel(techValue: number): string {
  return Technologies[techValue]; // Enum reverse mapping
}

getDifficultyLabel(diffValue: number): string {
  return DifficultyLevel[diffValue]; // Enum reverse mapping
}


  goToQuiz(id: string) {
    this.router.navigate(['/practice/intercative-quizzes'], {
      queryParams: { assignmentId: id }
    });
  }

  loadAssignments(): void {
    this.guestApiService.getAllAssignments().then(assignments => {
      this.assignments = assignments;
    }).catch(err => {
      console.error('Failed to load assignments', err);
    });
  }
}
<div class="p-6 md:px-16 text-white min-h-screen bg-[#0D1117]">
  <!-- Back link -->
  <a routerLink="/practice" class="text-sm text-gray-400 hover:text-orange-500 flex items-center mb-4">
    ‚Üê Back to Practice
  </a>

  <h1 class="text-3xl font-bold mb-6">All Assignments</h1>

  <!-- Filters -->
  <div class="flex flex-wrap gap-4 mb-6">
    <!-- Search bar (optional: implement logic in TS if needed) -->
    <input
      type="text"
      placeholder="Search assignments..."
      [(ngModel)]="searchTerm"
      class="bg-[#161B22] text-white px-4 py-2 rounded w-full md:w-1/2 focus:outline-none focus:ring-2 focus:ring-orange-600"
    />

    <!-- Technology Filter -->
    <select
      [(ngModel)]="selectedTech"
      class="bg-[#161B22] text-white px-4 py-2 rounded focus:outline-none focus:ring-2 focus:ring-orange-600"
    >
      <option value="All">All Technologies</option>
      <option *ngFor="let tech of technologies" [value]="tech">{{ tech }}</option>
    </select>

    <!-- Difficulty Filter -->
    <select
      [(ngModel)]="selectedDifficulty"
      class="bg-[#161B22] text-white px-4 py-2 rounded focus:outline-none focus:ring-2 focus:ring-orange-600"
    >
      <option value="All">All Difficulties</option>
      <option *ngFor="let d of difficultyLevels" [value]="d">{{ d }}</option>
    </select>
  </div>

  <!-- Result Summary -->
  <p class="mb-4 text-sm text-gray-300">
    Showing {{ filteredAssignments.length }} of {{ assignments.length }} assignments
  </p>

  <!-- No Data Message -->
  <p *ngIf="filteredAssignments.length === 0" class="text-gray-400 mt-4">
    üòï No assignments found for the selected filters.
  </p>

  <!-- Assignment Cards -->
  <div class="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
    <ng-container *ngFor="let assignment of filteredAssignments">
      <div
        class="bg-[#161B22] rounded-lg p-5 border border-gray-700 hover:border-orange-600 transition"
      >
        <div class="flex items-center justify-between mb-2">
          <div class="flex items-center gap-2 text-sm text-gray-400">
            <span class="w-2 h-2 rounded-full bg-yellow-400"></span>
            {{ technologies[assignment.technology] }}
          </div>

          <span
            class="px-3 py-0.5 rounded-full text-xs font-medium border"
            [ngClass]="difficultyMap[assignment.difficultyLevel].class"
          >
            {{ difficultyLevels[assignment.difficultyLevel] }}
          </span>
        </div>

        <h3 class="text-lg font-semibold mb-1">{{ assignment.title }}</h3>
        <p class="text-sm text-gray-400 mb-4">{{ assignment.description }}</p>

        <div class="flex items-center justify-between">
          <div class="flex items-center gap-2 text-sm text-gray-400">
            <svg xmlns="http://www.w3.org/2000/svg" class="h-4 w-4" fill="none"
              viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
              <path stroke-linecap="round" stroke-linejoin="round"
                d="M12 6v6l4 2m6 0a9 9 0 11-18 0 9 9 0 0118 0z" />
            </svg>
            <p>Showing {{ assignment.totalQuestions }} total questions</p>
          </div>

          <button
            class="bg-orange-600 text-white px-4 py-1.5 rounded hover:bg-orange-700"
            [routerLink]="['/practice/assignment', assignment.id]"
          >
            Start Quiz
          </button>
        </div>
      </div>
    </ng-container>
  </div>
</div>
