import { Component, Input, OnInit } from '@angular/core';
import { AssignmentQuestionsApiService } from '../services/assignment-questions-api.service';
import { AssignmentQuestionDto } from '../../models/assignment-question.model';

@Component({
  selector: 'app-question-detail',
  standalone: false,
  templateUrl: './question-detail.html',
  styleUrls: ['./question-detail.scss']
})
export class QuestionDetail implements OnInit {
  @Input() assignmentId!: string;
  @Input() assignmentTitle!: string;

  selectedAnswer: 'A' | 'B' | 'C' | 'D' | '' = ''; // initial value

  totalQuestions: number = 0;
  assignmentQuestions: AssignmentQuestionDto[] = [];

  // Form fields
  questionText: string = '';
  optionA: string = '';
  optionB: string = '';
  optionC: string = '';
  optionD: string = '';
  explanation: string = '';

  constructor(private assignmentQuestionsService: AssignmentQuestionsApiService) {}

  async ngOnInit(): Promise<void> 
    {
      if(this.assignmentId)
      {
        this.FetchAssignmentQuestions();
      }
    }

  ngOnChanges() {
    if (this.assignmentId) {
      this.FetchAssignmentQuestions();
    }
  }

  async createQuestion(): Promise<void> {
    if (!this.questionText || !this.selectedAnswer) {
      alert('Please fill in the question and correct answer.');
      return;
    }

    const newQuestion: AssignmentQuestionDto = {
      assignmentId: this.assignmentId,
      questionText: this.questionText,
      optionA: this.optionA,
      optionB: this.optionB,
      optionC: this.optionC,
      optionD: this.optionD,
      correctOption: this.selectedAnswer,
      explanation: this.explanation
    };

    try {
      await this.assignmentQuestionsService.addQuestion(this.assignmentId, newQuestion);
      this.FetchAssignmentQuestions();

      // Reset form
      this.clearForm();
    } catch (error) {
      console.error('Failed to create question', error);
      alert('Error creating question.');
    }
  }

  clearForm(): void {
    this.questionText = '';
    this.optionA = '';
    this.optionB = '';
    this.optionC = '';
    this.optionD = '';
    this.selectedAnswer = '';
    this.explanation = '';
  }

  async FetchAssignmentQuestions() 
  {
    try {
        if (this.assignmentId) {
          this.assignmentQuestions = await this.assignmentQuestionsService.getAssignmentQuestions(this.assignmentId);
          this.totalQuestions = this.assignmentQuestions.length;
        }
      } 
    catch (err) {
        console.error('Failed to fetch assignments', err);
      }
  }
}
<!-- Top Card with Assignment Info -->
<div class="shadow-lg mb-8 border-0 bg-gradient-to-br from-orange-50 to-orange-100 rounded-2xl hover:shadow-xl transition-all duration-300 hover:-translate-y-1">
  <div class="p-6">
    <div class="flex items-center justify-between">
      <div>
        <p class="text-2xl font-bold">Step 2: Manage Questions</p>
        <p class="text-sm text-gray-500">Assignment:
          <span class="text-orange-500 font-bold"> {{ assignmentTitle }}</span>
        </p>
      </div>
      <div class="p-3 text-gray-500">
        <p>Total Questions</p>
        <p class="text-orange-500 text-2xl ml-20 font-bold">{{ totalQuestions }}</p>
      </div>
    </div>
  </div>
</div>

<!-- Add Question Form Card -->
<div class="bg-white shadow-md rounded-2xl overflow-hidden max-w-5xl">
  <!-- Header -->
  <div class="bg-gradient-to-r from-blue-50 to-indigo-50 px-4 py-4">
    <h2 class="text-lg md:text-xl font-semibold text-gray-800 flex items-center gap-2">
      Add Question
    </h2>
    <p class="text-sm text-gray-500 mt-1">Enter question details below</p>
  </div>

  <!-- Form -->
  <div class="px-4 py-4 space-y-6">
    <!-- Question Text -->
    <div>
      <label class="text-sm font-medium text-gray-700">Question</label>
      <textarea
        rows="3"
        [(ngModel)]="questionText"
        name="questionText"
        placeholder="Enter the question text"
        class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm resize-none"
      ></textarea>
    </div>

    <!-- Options A–D -->
    <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
      <div>
        <label class="text-sm font-medium text-gray-700">Option A</label>
        <input
          type="text"
          [(ngModel)]="optionA"
          name="optionA"
          placeholder="Enter option A"
          class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm"
        />
      </div>
      <div>
        <label class="text-sm font-medium text-gray-700">Option B</label>
        <input
          type="text"
          [(ngModel)]="optionB"
          name="optionB"
          placeholder="Enter option B"
          class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm"
        />
      </div>
      <div>
        <label class="text-sm font-medium text-gray-700">Option C</label>
        <input
          type="text"
          [(ngModel)]="optionC"
          name="optionC"
          placeholder="Enter option C"
          class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm"
        />
      </div>
      <div>
        <label class="text-sm font-medium text-gray-700">Option D</label>
        <input
          type="text"
          [(ngModel)]="optionD"
          name="optionD"
          placeholder="Enter option D"
          class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm"
        />
      </div>
    </div>

    <!-- Correct Answer -->
    <div class="grid grid-cols-1 md:grid-cols-2 gap-4">
      <div>
        <label class="text-sm font-medium text-gray-700">Correct Answer</label>
        <select
          [(ngModel)]="selectedAnswer"
          name="selectedAnswer"
          class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm"
        >
          <option disabled selected value="">Select correct option</option>
          <option value="A">Option A</option>
          <option value="B">Option B</option>
          <option value="C">Option C</option>
          <option value="D">Option D</option>
        </select>
      </div>
    </div>

    <!-- Answer Explanation -->
    <div>
      <label class="text-sm font-medium text-gray-700">Answer Explanation</label>
      <textarea
        rows="3"
        [(ngModel)]="explanation"
        name="explanation"
        placeholder="Explain the correct answer"
        class="w-full mt-1 px-4 py-2 rounded-md border border-gray-300 focus:ring-2 focus:ring-orange-500 focus:outline-none text-sm resize-none"
      ></textarea>
    </div>

    <!-- Action Buttons -->
    <div class="flex flex-wrap gap-4 pt-2">
      <button
        type="button"
        (click)="createQuestion()"
        class="bg-orange-500 hover:bg-orange-600 text-white px-6 py-2 rounded-md text-sm font-semibold flex items-center gap-x-2"
      >
      <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round" class="lucide lucide-plus-icon lucide-plus"><path d="M5 12h14"/><path d="M12 5v14"/></svg>
        Create Question
      </button>
      <button
        type="reset"
        (click)="clearForm()"
        class="border border-gray-300 hover:bg-gray-100 text-gray-700 px-6 py-2 rounded-md text-sm font-medium"
      >
        Clear Form
      </button>
    </div>
  </div>
</div>
