import { Component, Input, AfterViewInit, ElementRef } from '@angular/core';
import hljs from 'highlight.js';

@Component({
  selector: 'app-code-block',
  standalone: false,
  templateUrl: './code-block.html',
  styleUrls: ['./code-block.scss']
})
export class CodeBlock implements AfterViewInit {
  @Input() language: string = '';
  @Input() code: string = '';

  constructor(private el: ElementRef) {}

  ngAfterViewInit() {
    hljs.highlightElement(this.el.nativeElement.querySelector('pre code'));
  }

  copyCode() {
    navigator.clipboard.writeText(this.code);
    alert('Code copied!');
  }
}
<div class="relative bg-gray-900 rounded mb-4">
  <button 
    (click)="copyCode()"
    class="absolute top-2 right-2 bg-orange-500 text-white px-2 py-1 text-xs rounded">
    Copy
  </button>

  <pre><code [class]="language">{{ code }}</code></pre>
</div>
