import { Component, OnInit } from '@angular/core';
import { jwtDecode, JwtPayload }   from 'jwt-decode';
import { RequestContextService } from '../../../../core/services/request.context';

@Component({
  selector: 'app-header',
  standalone: false,
  templateUrl: './header.html',
  styleUrl: './header.scss'
})
export class Header implements OnInit {
  isLoggedIn = false;
  privileges: string[] = [];
  features: string[] = [];
  
  constructor(private requestContext: RequestContextService) {} 

  ngOnInit(): void {
    const token = localStorage.getItem('access_token');
    this.isLoggedIn = this.requestContext.isLoggedIn;
  }
}
<header class="flex justify-between items-center h-16 px-6 bg-igknightHeader text-white border-b border-white/10">
  <!-- Left: Logo -->
  <div class="flex items-center space-x-2">
    <div class="w-8 h-8 bg-gradient-to-r from-orange-500 to-red-500 rounded-lg flex items-center justify-center">
      <span class="text-white font-bold text-lg">ðŸ”¥</span>
    </div>
    <span class="text-2xl font-bold text-white">IGKNIGHT</span>
    <!--Heroes -->
    <a routerLink="/heroes"   class="px-4 py-2 ml-28 rounded-full text-white bg-transparent transition duration-300 ease-in-out hover:bg-orange-500 text-white hover:shadow-lg">Meet the Trailblazers â†’</a>
  </div>
  

  <!-- Right: Navigation Buttons -->
<nav class="flex items-center space-x-6">
  <a routerLink="/courses" class="hover:text-orange-500 transition duration-200">Courses</a>
  <a routerLink="/practice" class="hover:text-orange-500 transition duration-200">Practice</a>

  <button 
    *ngIf="!isLoggedIn"
    [routerLink]="'/login'" 
    class="bg-orange-500 hover:bg-orange-600 text-white px-4 py-1 rounded-md transition duration-200"
  >
    Login
  </button>

  <button 
    *ngIf="isLoggedIn"
    [routerLink]="'/profile'" 
    class="bg-orange-500 hover:bg-orange-600 text-white px-4 py-1 rounded-md transition duration-200"
  >
    View Profile
  </button>
</nav>
</header>
