import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home-hero',
  standalone: false,
  templateUrl: './home-hero.html',
  styleUrl: './home-hero.scss'
})
export class HomeHero {
  /**
   *
   */
  constructor(private router: Router) {}

  navigateToCourses(){
    this.router.navigate(['/documentation/fullstack']);
  }
}
<section class="text-center py-20 bg-transparent">
  <h1 class="text-4xl md:text-5xl font-bold text-white mb-4">
    Ignite Your Tech Journey
  </h1>
  <p class="text-lg text-gray-300 mb-6 max-w-2xl mx-auto">
    Learn coding, prepare for interviews, and build amazing projects with IgKnight.
  </p>
  <div class="flex justify-center gap-4">
    <button class="bg-orange-600 text-white px-6 py-3 rounded-lg font-medium hover:bg-orange-700 transition"
    (click)="navigateToCourses()">
      Start Learning
    </button>
    <button class="border border-orange-500 text-orange-400 px-6 py-3 rounded-lg font-medium hover:bg-orange-900 hover:text-white transition">
      Explore Assignments
    </button>
  </div>
</section>
