import { Component , ViewChild, ElementRef, Input } from '@angular/core';
import { TECH_PREVIEWS, TechPreview } from '../../data/tech-preview.data';
import { Router } from '@angular/router';

@Component({
  selector: 'app-tech-preview-grid',
  templateUrl: './tech-preview-grid.html',
  styleUrl: './tech-preview-grid.scss',
  standalone: false,
})
export class TechPreviewGrid {
    @ViewChild('scrollContainer', { static: false }) scrollContainer!: ElementRef;
    techPreviews: TechPreview[] = TECH_PREVIEWS;
  constructor(private router: Router) {}
  
  showLeftArrow = false;
  showRightArrow = false;

  ngAfterViewInit(): void {
    this.checkScroll();
  }

  scrollLeft(): void {
    this.scrollContainer.nativeElement.scrollBy({ left: -300, behavior: 'smooth' });
  }

  scrollRight(): void {
    this.scrollContainer.nativeElement.scrollBy({ left: 300, behavior: 'smooth' });
  }

  checkScroll(): void {
    const el = this.scrollContainer.nativeElement;
    this.showLeftArrow = el.scrollLeft > 0;
    this.showRightArrow = el.scrollLeft + el.clientWidth < el.scrollWidth;
  }

  navigateToCourses(track: TechPreview): void {
    this.router.navigate([track.route]);
  }
}
<section class="py-14 px-4 relative bg-black">
  <div class="max-w-7xl mx-auto relative">
    <!-- Heading -->
    <div class="text-left mb-6">
      <h2 class="text-3xl font-bold text-white mb-4">Trending Tracks</h2>
    </div>

    <!-- Left Scroll Button -->
    <button 
      (click)="scrollLeft()"
      aria-label="Scroll Left"
      class="absolute -left-10 top-1/2 transform -translate-y-1/2 z-20 bg-orange-500 text-white p-2 rounded-full shadow hover:bg-orange-600 transition-all"
    >
      <svg xmlns="http://www.w3.org/2000/svg" class="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M15 19l-7-7 7-7" />
      </svg>
    </button>

    <!-- Right Scroll Button -->
    <button 
      (click)="scrollRight()"
      aria-label="Scroll Right"
      class="absolute -right-10 top-1/2 transform -translate-y-1/2 z-20 bg-orange-500 text-white p-2 rounded-full shadow hover:bg-orange-600 transition-all"
    >
      <svg xmlns="http://www.w3.org/2000/svg" class="w-5 h-5" fill="none" viewBox="0 0 24 24" stroke="currentColor">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M9 5l7 7-7 7" />
      </svg>
    </button>

    <!-- Scrollable Cards Container -->
    <div
      #scrollContainer
      role="list"
      aria-label="Technology Tracks"
      class="flex gap-6 overflow-x-auto overflow-y-hidden scroll-smooth snap-x snap-mandatory px-2 py-4 custom-scroll touch-auto"
    >
      <div
        *ngFor="let track of techPreviews"
        role="button"
        tabindex="0"
        (click)="navigateToCourses(track)"
        (keydown.enter)="navigateToCourses(track)"
        class="min-w-[200px] sm:min-w-[240px] h-[160px] bg-slate-900 border border-orange-500 rounded-xl p-4 flex flex-col justify-between snap-start hover:bg-slate-800 transition-all shadow hover:shadow-lg cursor-pointer focus:outline-none focus:ring-2 focus:ring-orange-500"
      >
        <h3 class="text-white text-lg font-semibold truncate">{{ track.title }}</h3>
        <p class="text-sm text-gray-400">{{ track.students }} students</p>
        <span class="self-start px-2 py-1 text-xs font-medium rounded-full border border-orange-500 text-orange-500">
          {{ track.level }}
        </span>
      </div>
    </div>
  </div>
</section>
