import { Component } from '@angular/core';

@Component({
  selector: 'app-motivation-header',
  standalone: false,
  templateUrl: './motivation-header.html',
  styleUrl: './motivation-header.scss'
})
export class MotivationHeader {

}
<div class="text-center">
  <h2 class="text-3xl mt-5 md:text-4xl font-bold text-white">
    From Zero to <span class="text-orange-500">Hero</span>
  </h2>
  <p class="mt-2 text-white/70 text-sm md:text-base">
    Get inspired by the legends who started with nothing but curiosity and changed the world through code
  </p>
</div>
