import { Component, OnInit, AfterViewInit } from '@angular/core';
import { ActivatedRoute, Route, Router } from '@angular/router';
import { DocumentationService } from '../../services/documentation.service';
import hljs from 'highlight.js';
import { TopicContent } from '../../../../models/topic.model';

@Component({
  selector: 'app-topic-page',
  standalone: false,
  templateUrl: './topic-page.html',
})
export class TopicPage implements OnInit, AfterViewInit {
  technology!: string;
  slug?: string;  // notice this is now optional
  topicContent?: TopicContent;
  copiedIndex: number | null = null;

  constructor(
    private route: ActivatedRoute,
    private documentationService: DocumentationService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.technology = params['technology'];
      this.slug = params['slug'];  // may be undefined
      this.topicContent = undefined; // Clear before loading new
      if (this.slug) {
        this.loadContent();
      } else {
        this.slug = this.technology + '-introduction'; // Default slug if none provided
        this.router.navigate(['/documentation', this.technology, this.slug]);
        this.loadContent();
      }
    });
  }

  loadContent() {
  this.topicContent = undefined; // Clear old content
  this.documentationService.getTopicContent(this.technology, this.slug!)
    .subscribe(content => {
      this.topicContent = content;
      setTimeout(() => this.highlightCode(), 0);
    });
}


  navigateToTryEditor() {
    this.router.navigate(['/try-editor', this.technology]);
  }

  copyCode(code: string, index: number) {
  navigator.clipboard.writeText(code).then(() => {
    this.copiedIndex = index;
    setTimeout(() => {
      this.copiedIndex = null;
    }, 1000); // Revert button text after 3 seconds
  });
}


  ngAfterViewInit(): void {
    this.highlightCode();
  }

  highlightCode() {
    document.querySelectorAll('pre code').forEach((block) => {
      hljs.highlightElement(block as HTMLElement);
    });
  }
}
<div *ngIf="topicContent">

  <!-- Header -->
  <div class="flex justify-between items-center mb-4">
  <h1 class="text-2xl font-bold text-white">{{ topicContent.title }}</h1>

  <button
    *ngIf="technology === 'html' || technology === 'css' || technology === 'js'"
    class="bg-orange-500 hover:bg-orange-600 text-white font-semibold py-2 px-4 rounded transition-all duration-300"
    (click)="navigateToTryEditor()">
    Try Editor
  </button>
</div>


  <!-- Main Content -->
  <div class="mb-6 text-gray-300" [innerHTML]="topicContent.content"></div>

  <!-- Code Samples -->
  <div *ngFor="let code of topicContent.codeSamples; let i = index" class="mb-6 relative">
    <pre class="bg-[#1F2937] p-4 rounded-lg overflow-auto text-green-400">
      <code>{{ code.code }}</code>
    </pre>
    <button

      (click)="copyCode(code.code, i)"
      class="absolute top-2 right-2 bg-gray-700 hover:bg-gray-600 text-white text-xs px-2 py-1 rounded z-10"
    >
      {{ copiedIndex === i ? 'âœ… Copied!' : 'ðŸ“‹ Copy' }}
    </button>
  </div>

  <!-- Pro Tip (Optional) -->
  <div *ngIf="topicContent.proTip" class="bg-blue-900 text-blue-200 p-4 rounded-lg flex items-center">
    ðŸ’¡ <span class="ml-2">{{ topicContent.proTip }}</span>
  </div>

</div>
