import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-interview-prep',
  standalone: false,
  templateUrl: './interview-prep.html',
  styleUrl: './interview-prep.scss'
})
export class InterviewPrep {
  constructor(private router: Router) {}

  topics = [
    {
      title: 'Data Structures & Algorithms',
      icon: 'üß†',
      description: 'Master the most frequently asked DSA questions with patterns.',
      slug: 'dsa'
    },
    {
      title: 'System Design',
      icon: 'üï∏Ô∏è',
      description: 'Understand scalable architectures, databases, and real-world systems.',
      slug: 'system-design'
    },
    {
      title: 'HR + Behavioral',
      icon: 'üó£Ô∏è',
      description: 'Ace HR interviews with real-world scenarios and sample answers.',
      slug: 'hr'
    },
    {
      title: 'DBMS & SQL',
      icon: 'üóÉÔ∏è',
      description: 'Practice queries, normalization, transactions, indexing & more.',
      slug: 'dbms'
    },
    {
      title: 'Operating Systems',
      icon: 'üñ•Ô∏è',
      description: 'Brush up on processes, memory, concurrency, scheduling etc.',
      slug: 'os'
    },
    {
      title: 'Networking',
      icon: 'üåê',
      description: 'Covers OSI layers, protocols, firewalls, HTTP, DNS & more.',
      slug: 'networking'
    }
  ];

  startPrep(slug: string) {
    this.router.navigate(['/practice/interview-prep', slug]); // Placeholder
  }
}
<div class="min-h-screen bg-gray-900 text-white py-10 px-6">
  <div class="max-w-6xl mx-auto">
    <h1 class="text-3xl font-bold mb-8 text-center">üéØ Interview Preparation</h1>

    <div class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
      <div
        *ngFor="let topic of topics"
        class="bg-gray-800 rounded-xl p-6 hover:scale-[1.02] transition-all shadow-md">
        
        <div class="flex items-center gap-3 mb-4">
          <div class="text-3xl">{{ topic.icon }}</div>
          <h2 class="text-xl font-semibold">{{ topic.title }}</h2>
        </div>
        
        <p class="text-gray-400 text-sm mb-4">{{ topic.description }}</p>
        
        <button
          class="bg-orange-500 hover:bg-orange-600 px-4 py-2 rounded text-white font-semibold"
          (click)="startPrep(topic.slug)">
          üöÄ Start Prep
        </button>
      </div>
    </div>
  </div>
</div>
