import { Component } from '@angular/core';

@Component({
  selector: 'app-coding-challenges-page',
  standalone: false,
  templateUrl: './coding-challenges.page.html',
  styleUrl: './coding-challenges.page.scss'
})
export class CodingChallengesPage {
  challenges = [
    {
      id: 1,
      title: 'Two Sum',
      difficulty: 'Easy',
      category: 'Arrays',
      description: 'Given an array of integers nums and an integer target, return indices of the two numbers such that they add up to target.',
      tags: ['Array', 'Hash Table'],
      likes: 42853,
      acceptance: 49.2
    },
    {
      id: 2,
      title: 'Longest Palindromic Substring',
      difficulty: 'Medium',
      category: 'Dynamic Programming',
      description: 'Given a string s, return the longest palindromic substring in s.',
      tags: ['String', 'Dynamic Programming'],
      likes: 27845,
      acceptance: 32.4
    },
    {
      id: 3,
      title: 'Valid Parentheses',
      difficulty: 'Easy',
      category: 'Strings',
      description: 'Given a string s, return true if the string is valid (open and close brackets properly).',
      tags: ['String', 'Stack'],
      likes: 20563,
      acceptance: 40.1
    }
  ];
}
